{
  "query": "query {
  conversations {
    conversations {
      id
      tabName
      llmModel
      isPrivate
      createdAt
      updatedAt
      messageCount
      messages {
        id
        text
        sender
        timestamp
      }
    }
    count
  }
}"
}

{
  "query": "query GetConversation($id: String!) {
  conversation(id: $id) {
    id
    tabName
    llmModel
    isPrivate
    createdAt
    updatedAt
    messageCount
    messages {
      id
      conversationId
      text
      sender
      timestamp
    }
  }
}",
  "variables": {
    "id": "{{conversation_id}}"
  }
}

{
  "query": "query GetPrivateConversation($id: String!, $password: String) {
  conversation(id: $id, password: $password) {
    id
    tabName
    llmModel
    isPrivate
    createdAt
    updatedAt
    messageCount
    messages {
      id
      conversationId
      text
      sender
      timestamp
    }
  }
}",
  "variables": {
    "id": "{{conversation_id}}",
    "password": "{{password}}"
  }
}

{
  "query": "query GetMessages($conversationId: String!, $password: String) {
  messages(conversationId: $conversationId, password: $password) {
    id
    conversationId
    text
    sender
    timestamp
  }
}",
  "variables": {
    "conversationId": "{{conversation_id}}",
    "password": "{{password}}"
  }
}

{
  "query": "mutation CreateConversation($input: CreateConversationInput!) {
  createConversation(input: $input) {
    message
    conversation {
      id
      tabName
      llmModel
      isPrivate
      createdAt
      updatedAt
      messageCount
    }
  }
}",
  "variables": {
    "input": {
      "id": "new-conversation-456",
      "tabName": "My New Chat",
      "llmModel": "llama3.1:8b"
    }
  }
}

{
  "query": "mutation CreatePrivateConversation($input: CreateConversationInput!) {
  createConversation(input: $input) {
    message
    conversation {
      id
      tabName
      llmModel
      isPrivate
      createdAt
      updatedAt
      messageCount
    }
  }
}",
  "variables": {
    "input": {
      "id": "private-conversation-789",
      "tabName": "Secret Chat",
      "llmModel": "llama3.1:8b",
      "password": "{{password}}"
    }
  }
}

{
  "query": "mutation AddUserMessage($input: AddMessageInput!) {
  addMessage(input: $input) {
    message
    userMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmMessage
    llmModel
    error
    llmResponseTime
  }
}",
  "variables": {
    "input": {
      "conversationId": "{{conversation_id}}",
      "text": "This is a user message without LLM response",
      "sender": "user"
    }
  }
}

{
  "query": "mutation AddMessageWithLLM($input: AddMessageInput!) {
  addMessage(input: $input) {
    message
    userMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmModel
    error
    llmResponseTime
  }
}",
  "variables": {
    "input": {
      "conversationId": "{{conversation_id}}",
      "text": "Hello! How are you today?",
      "sender": "user",
      "llmModel": "llama3.1:8b"
    }
  }
}

{
  "query": "mutation AddMessageToPrivate($input: AddMessageInput!) {
  addMessage(input: $input) {
    message
    userMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmModel
    error
    llmResponseTime
  }
}",
  "variables": {
    "input": {
      "conversationId": "private-conversation-789",
      "text": "This is a secret message",
      "sender": "user",
      "password": "{{password}}",
      "llmModel": "llama3.1:8b"
    }
  }
}

{
  "query": "mutation AddLLMMessage($input: AddMessageInput!) {
  addMessage(input: $input) {
    message
    userMessage {
      id
      conversationId
      text
      sender
      timestamp
    }
    llmMessage
    llmModel
    error
    llmResponseTime
  }
}",
  "variables": {
    "input": {
      "conversationId": "{{conversation_id}}",
      "text": "This is a direct LLM message",
      "sender": "llm"
    }
  }
}

{
  "query": "mutation VerifyPassword($input: VerifyPasswordInput!) {
  verifyPassword(input: $input) {
    message
    conversationId
    tabName
  }
}",
  "variables": {
    "input": {
      "conversationId": "private-conversation-789",
      "password": "{{password}}"
    }
  }
}
